import React from "react";
import { Button, Dropdown, DropdownMenu, DropdownToggle } from "reactstrap";
import "bootstrap/dist/css/bootstrap.css";
import "../../static/foundation-6.5.1-custom/css/foundation.min.css";

export default class SearchForm extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: false
    };
  }
  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }

  renderDropDown(title, desc, content) {
    return (
      <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
        <DropdownToggle caret>
          This dropdown's menu is right-aligned
        </DropdownToggle>
        <DropdownMenu right>
          <DropdownItem header>Header</DropdownItem>
          <DropdownItem disabled>Action</DropdownItem>
          <DropdownItem>Another Action</DropdownItem>
          <DropdownItem divider />
          <DropdownItem>Another Action</DropdownItem>
        </DropdownMenu>
      </Dropdown>
    );
  }

  render() {
    return (
      <div className="grid-container">
        <div className="grid-x">
          <div className="small-12 medium-6 large-3 cellio">
            <Button color="danger">Depart!</Button>
          </div>
          <div className="small-12 medium-6 large-3 cellio">
            <Button color="danger">Arrive!</Button>
          </div>
          <div className="small-12 medium-6 large-3 cellio">
            <Button color="danger">Date!</Button>
          </div>
          <div className="small-12 medium-6 large-3 cellio">
            <div className="grid-x">
              <div className="small-4">
                <Button color="danger">Date!</Button>
              </div>
              <div className="small-8">
                <Button color="danger">Date!</Button>
              </div>
            </div>
          </div>
        </div>
        <style jsx>{`
          .cellio {
            background-color: dimgrey;
            padding: 5px;
          }
        `}</style>
      </div>
    );
  }
}
