import React from "react";
import { Button, Input } from "reactstrap";
import Datetime from "react-datetime";
import "bootstrap/dist/css/bootstrap.css";
import "../../static/foundation-6.5.1-custom/css/foundation.min.css";
import "react-datetime/css/react-datetime.css";

const arrivalTownList = ["one", "two", "three", "four", "five", "six", "seven"];

export default class SearchForm extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    // Let's use the static moment reference in the Datetime component
    var yesterday = Datetime.moment().subtract(1, "day");
    var valid = function(current) {
      return current.isAfter(yesterday);
    };
    return (
      <div className="grid-container searchform">
        <div className="grid-x">
          <div className="small-12 medium-6 large-3 searchItem padding5">
            <Input type="select" bsSize="lg">
              {arrivalTownList.map(town => (
                <option>{town}</option>
              ))}
            </Input>
          </div>
          <div className="small-12 medium-6 large-3 searchItem padding5">
            <Input type="select" bsSize="lg">
              {arrivalTownList.map(town => (
                <option>{town}</option>
              ))}
            </Input>
          </div>
          <div className="small-12 medium-6 large-3 searchItem padding5">
            <Datetime
              inputProps={{ placeholder: "Date" }}
              isValidDate={valid}
            />
          </div>
          <div className="small-12 medium-6 large-3 searchItem padding5">
            <div className="grid-x">
              <div
                className="small-4 searchItem"
                style={{ paddingRight: "8px" }}
              >
                <Input type="number" bsSize="sm"></Input>
              </div>
              <InputGroup>
                <InputGroupAddon addonType="prepend">
                  <InputGroupText>$</InputGroupText>
                  <InputGroupText>$</InputGroupText>
                </InputGroupAddon>
                <Input placeholder="Dolla dolla billz yo!" />
                <InputGroupAddon addonType="append">
                  <InputGroupText>$</InputGroupText>
                  <InputGroupText>$</InputGroupText>
                </InputGroupAddon>
              </InputGroup>
              <div className="small-8">
                <Button color="#484848" size="lg" block>
                  Search
                </Button>
              </div>
            </div>
          </div>
        </div>
        <style jsx>{`
          .padding5 {
            padding: 5px;
          }
          .searchItem {
            display: flex;
            flex-direction: column;
            justify-content: center;
          }
          .searchform {
            background-color: rgba(0, 0, 0, 0.3);
            padding: 10px;
          }
          .searchbtn {
            background-color: orange !important;
            border: solid white 5px !important;
          }
        `}</style>
      </div>
    );
  }
}
